import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap'; // Import Modal
import { useNavigate } from 'react-router-dom';

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [isCreatePopupOpen, setIsCreatePopupOpen] = useState(false); // Popup state
  const [newProduct, setNewProduct] = useState({}); // State for new product
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  // Function to fetch data and initialize
  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      setProducts(response.data.items || []);
      setTotalPages(response.data.totalPages || 1);
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Create Product Handlers
  const handleCreateClick = () => setIsCreatePopupOpen(true);

  const handleCreateProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveNewProduct = async () => {
    try {
      const response = await axiosInstance.post('Product', newProduct);
      if (response.status === 201) {
        setIsCreatePopupOpen(false);
        fetchProducts(currentPage, pageSize); // Refresh product list
      }
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm!", error);
    }
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={handleCreateClick}>
          Create Product
        </Button>
      )}

      <Table striped bordered hover>
        {/* Table content */}
      </Table>

      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Create Product Modal */}
      <Modal show={isCreatePopupOpen} onHide={() => setIsCreatePopupOpen(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                placeholder="Enter product name"
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Category</Form.Label>
              <Form.Control as="select" name="categoryId" onChange={handleCreateProductChange}>
                <option value="">Select category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="text"
                name="productDescription"
                placeholder="Enter description"
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                placeholder="Enter price"
                onChange={handleCreateProductChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setIsCreatePopupOpen(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSaveNewProduct}>
            Save Product
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
