import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table } from 'react-bootstrap';

// Setup Axios Instance
const getAuthToken = () => localStorage.getItem('token');
const getUserRole = () => localStorage.getItem('roles'); // Get user role from localStorage

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      const token = getAuthToken();
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

  // Check if the user is logged in and if the user is an admin
  useEffect(() => {
    const token = getAuthToken();
    const role = getUserRole();

    if (token && role && role.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login'); // Redirect to login page if not logged in or not admin
    }
  }, [navigate]);

  const CategoryPage = () => {
    const [categories, setCategories] = useState([]);
  
    useEffect(() => {
      // Fetch categories from API
      axios.get('http://localhost:5203/api/category', {
        headers: {
          // Đảm bảo bạn không quên thêm bất kỳ header nào nếu cần thiết (ví dụ Authorization)
        }
      })
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the categories!", error);
      });
    }, []);
  
    return (
      <div>
        <h2>Manage Categories</h2>
        <Button variant="primary" onClick={() => alert('Create new category')}>Create Category</Button>
        <Table striped bordered hover className="mt-4">
          <thead>
            <tr>
              <th>Category ID</th>
              <th>Category Name</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {categories.map((category) => (
              <tr key={category.categoryId}>
                <td>{category.categoryId}</td>
                <td>{category.categoryName}</td>
                <td>{category.categoryDescription}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    );
  };
  
  export default CategoryPage;
