import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => Promise.reject(error)
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [modalData, setModalData] = useState(null); // Dùng chung cho Create và Edit
  const [isModalVisible, setIsModalVisible] = useState(false);
  const navigate = useNavigate();

  // Kiểm tra quyền admin
  useEffect(() => {
    if (token && roles?.includes('Admin User')) {
      // Đăng nhập và có quyền admin
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch Data
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data?.items) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Handlers
  const handleOpenModal = (product = null) => {
    setModalData(product || { productName: '', categoryId: '', productDescription: '', productPrice: '', productImageUrl: '' });
    setIsModalVisible(true);
  };

  const handleCloseModal = () => {
    setIsModalVisible(false);
    setModalData(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setModalData((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setModalData((prev) => ({ ...prev, productImageUrl: response.data.imageUrl }));
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSave = async () => {
    const isEditing = !!modalData.productId;

    try {
      if (isEditing) {
        await axiosInstance.put(`Product/${modalData.productId}`, modalData);
      } else {
        await axiosInstance.post('Product', modalData);
      }

      fetchProducts(currentPage, pageSize);
      handleCloseModal();
    } catch (error) {
      console.error("Có lỗi khi lưu sản phẩm!", error);
    }
  };

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      <Button variant="primary" className="mb-3" onClick={() => handleOpenModal()}>
        Create Product
      </Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>{product.productName}</td>
              <td>{product.category?.categoryName || 'N/A'}</td>
              <td>{product.productDescription}</td>
              <td>{product.productPrice}</td>
              <td>
                {product.productImageUrl && (
                  <Image src={`http://localhost:5072${product.productImageUrl}`} alt={product.productName} width={50} />
                )}
              </td>
              <td>
                <Button variant="warning" onClick={() => handleOpenModal(product)}>
                  Edit
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Modal */}
      <Modal show={isModalVisible} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{modalData?.productId ? 'Edit Product' : 'Create Product'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={modalData?.productName || ''}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="categoryId">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={modalData?.categoryId || ''}
                onChange={handleInputChange}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="productDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                name="productDescription"
                value={modalData?.productDescription || ''}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="productPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={modalData?.productPrice || ''}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="productImage">
              <Form.Label>Image</Form.Label>
              <Form.Control type="file" onChange={handleImageChange} />
              {modalData?.productImageUrl && (
                <Image
                  src={`http://localhost:5072${modalData.productImageUrl}`}
                  alt="Preview"
                  width={100}
                  className="mt-2"
                />
              )}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
