import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [newProduct, setNewProduct] = useState(null); // State to track the new product
  const navigate = useNavigate(); // useNavigate hook for redirecting

  const token = localStorage.getItem('token');
  const roles = JSON.parse(localStorage.getItem('roles'));

  useEffect(() => {
    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login');
    } else {
      fetchCategories();
      fetchProducts(currentPage, pageSize);
    }
  }, [navigate, currentPage, pageSize]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  // Handle "Create Product" button click
  const handleCreateProductClick = () => {
    setNewProduct({ productId: 'new', productName: '', category: {}, productDescription: '', productPrice: '', productImageUrl: '' });
    setCurrentPage(totalPages); // Navigate to last page
  };
  // Handlers
  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    if (productId === 'new') {
      setNewProduct((prev) => ({ ...prev, [name]: value }));
    } else {
      setProducts((prev) =>
        prev.map((product) =>
          product.productId === productId ? { ...product, [name]: value } : product
        )
      );
    }
  };

  const handleSaveClick = async (productId) => {
    const product = productId === 'new' ? newProduct : products.find((p) => p.productId === productId);

    try {
      const response = await axiosInstance.post('Product', product);
      fetchProducts(currentPage, pageSize); // Refresh product list
      setNewProduct(null); // Clear new product state
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setNewProduct(null); // Clear new product state
  };

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      {token && roles && (
        <Button variant="primary" className="mb-3" onClick={handleCreateProductClick}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {roles && roles.includes('Admin User') && <th>Actions</th>} {/* Only show Actions if admin */}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ''}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category?.categoryName || 'N/A'
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productPrice
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <input
                      type="file"
                      onChange={(e) => handleInputChange(e, product.productId)}
                      style={{ display: 'none' }}
                      id={`upload_${product.productId}`}
                    />
                  ) : (
                    product.productImageUrl && <Image src={`http://localhost:5203${product.productImageUrl}`} width={150} />
                  )}
                </td>
                <td>{product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}</td>
                <td>{product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Button variant="success" onClick={() => handleSaveClick(product.productId)}>
                      Save
                    </Button>
                  ) : (
                    <Button variant="warning" onClick={() => handleEditClick(product.productId)}>
                      Edit
                    </Button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
          {newProduct && (
            <tr>
              <td>New</td>
              <td>
                <Form.Control
                  type="text"
                  name="productName"
                  value={newProduct.productName}
                  onChange={(e) => handleInputChange(e, 'new')}
                />
              </td>
              <td>
                <Form.Control
                  as="select"
                  name="category"
                  value={newProduct.category?.categoryId || ''}
                  onChange={(e) => handleInputChange(e, 'new')}
                >
                  {categories.map((category) => (
                    <option key={category.categoryId} value={category.categoryId}>
                      {category.categoryName}
                    </option>
                  ))}
                </Form.Control>
              </td>
              <td>
                <Form.Control
                  type="text"
                  name="productDescription"
                  value={newProduct.productDescription}
                  onChange={(e) => handleInputChange(e, 'new')}
                />
              </td>
              <td>
                <Form.Control
                  type="number"
                  name="productPrice"
                  value={newProduct.productPrice}
                  onChange={(e) => handleInputChange(e, 'new')}
                />
              </td>
              <td>
                <input
                  type="file"
                  onChange={(e) => handleInputChange(e, 'new')}
                  style={{ display: 'none' }}
                  id={`upload_new`}
                />
              </td>
              <td colSpan="2">
                <Button variant="success" onClick={() => handleSaveClick('new')}>Save</Button>
                <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
              </td>
            </tr>
          )}
        </tbody>
      </Table>

      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
