// src/pages/CustomerPage.js
import React, { useEffect, useState } from 'react';

const CustomerPage = () => {
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null);

  useEffect(() => {
    // Lấy token và role từ localStorage
    const storedToken = localStorage.getItem('token');
    const storedRole = localStorage.getItem('roles');
    
    setToken(storedToken); // Cập nhật token vào state
    setRole(storedRole);   // Cập nhật role vào state
  }, []); // Chỉ chạy 1 lần khi component được render lần đầu

  return (
    <div>
      <h2>View Customers</h2>
      <p>This page will display a list of customers.</p>
      
      <div>
        <h4>User Information:</h4>
        <p><strong>Token:</strong> {token ? token : 'No token found'}</p>
        <p><strong>Role:</strong> {role ? role : 'No role found'}</p>
      </div>
    </div>
  );
};

export default CustomerPage;
