import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [isEditing, setIsEditing] = useState(null);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    categoryId: '',
    productDescription: '',
    productPrice: '',
    productImageUrl: ''
  }); // State to handle new product form input
  const [showCreateModal, setShowCreateModal] = useState(false); // State to handle modal visibility
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // State to track if user is an admin
  const navigate = useNavigate(); // useNavigate hook for redirecting

  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login'); // Redirect to login page if not logged in or not admin
    }
  }, [navigate]);

  // Fetch Categories
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  // Fetch Products
  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage);
  }, [currentPage]);

  // Handle Edit
  const handleEditClick = (productId) => {
    const productToEdit = products.find(p => p.productId === productId);
    setNewProduct({
      productName: productToEdit.productName,
      categoryId: productToEdit.category?.categoryId || '',
      productDescription: productToEdit.productDescription,
      productPrice: productToEdit.productPrice,
      productImageUrl: productToEdit.productImageUrl
    });
    setIsEditing(productId);
    setShowCreateModal(true); // Open modal for editing
  };

  const handleCreateModalClose = () => {
    setShowCreateModal(false);
    setIsEditing(null); // Reset editing state
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  // Check if form is valid (for enabling/disabling the save button)
  const isFormValid = () => {
    return newProduct.productName.trim() !== '' && newProduct.categoryId !== '';
  };

  const handleSaveProduct = async () => {
    try {
      if (isEditing) {
        // Update existing product
        await axiosInstance.put(`Product/${isEditing}`, newProduct);
      } else {
        // Create new product
        await axiosInstance.post('Product', newProduct);
      }
      setShowCreateModal(false);
      fetchProducts(currentPage); // Refresh product list
    } catch (error) {
      console.error("Có lỗi khi lưu sản phẩm!", error);
    }
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Create Product
        </Button>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Created Date</th>
            <th>Updated Date</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>{product.productName}</td>
                <td>{product.category?.categoryName || 'N/A'}</td>
                <td>{product.productDescription}</td>
                <td>{product.productPrice}</td>
                <td>
                  {product.productImageUrl && (
                    <Image
                      src={`http://localhost:5072${product.productImageUrl}`}
                      alt={product.productName}
                      width={50}
                    />
                  )}
                </td>
                <td>{product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}</td>
                <td>{product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    <Button variant="warning" onClick={() => handleEditClick(product.productId)}>
                      Edit
                    </Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>

      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Modal for Create/Edit Product */}
      <Modal show={showCreateModal} onHide={handleCreateModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Product' : 'Create Product'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product name"
                name="productName"
                value={newProduct.productName}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleInputChange}
              >
                <option value="">Select category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="formProductDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product description"
                name="productDescription"
                value={newProduct.productDescription}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formProductPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product price"
                name="productPrice"
                value={newProduct.productPrice}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formProductImage">
              <Form.Label>Product Image</Form.Label>
              <Form.Control
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onload = () => {
                      setNewProduct((prev) => ({
                        ...prev,
                        productImageUrl: reader.result,
                      }));
                    };
                    reader.readAsDataURL(file);
                  }
                }}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCreateModalClose}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={handleSaveProduct}
            disabled={!isFormValid()} // Disable Save button if form is invalid
          >
            {isEditing ? 'Update Product' : 'Save Product'}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
