import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

// Setup Axios Instance
const getAuthToken = () => localStorage.getItem('token');
const getUserRole = () => localStorage.getItem('roles'); // Get user role from localStorage

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      const token = getAuthToken();
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const [editedProduct, setEditedProduct] = useState({
    productId: '',
    productName: '',
    categoryId: '',
    description: '',
    price: '',
    productImageUrl: ''
  });
  const navigate = useNavigate(); // useNavigate hook for redirecting

  // Kiểm tra token và role khi component được mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login'); // Điều hướng về trang login nếu không có quyền truy cập
    } else {
      fetchCategories();
      fetchProducts();
    }
  }, [navigate]);

  // Fetch Categories
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  // Fetch Products
  const fetchProducts = async () => {
    try {
      const response = await axiosInstance.get('Product');
      setProducts(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
    }
  };

  // Handlers for editing and creating products
  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCategoryChange = (e) => {
    setEditedProduct((prev) => ({
      ...prev,
      categoryId: e.target.value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setEditedProduct((prev) => ({
            ...prev,
            productImageUrl: response.data.imageUrl
          }));
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  // Save the edited product
  const handleSaveClick = async () => {
    try {
      const product = { ...editedProduct };
      await axiosInstance.put(`Product/${editedProduct.productId}`, product);
      fetchProducts();
      setIsEditing(null);
    } catch (error) {
      console.error("Có lỗi khi cập nhật sản phẩm!", error);
    }
  };

  // Cancel edit mode
  const handleCancelClick = () => {
    setIsEditing(null);
    setEditedProduct({
      productId: '',
      productName: '',
      categoryId: '',
      description: '',
      price: '',
      productImageUrl: ''
    });
  };

  // Handle Create Product
  const handleCreateProduct = () => {
    setIsEditing('new');
    setEditedProduct({
      productId: '',
      productName: '',
      categoryId: '',
      description: '',
      price: '',
      productImageUrl: ''
    });
  };

  const handleCreateSaveClick = async () => {
    try {
      await axiosInstance.post('Product', editedProduct);
      fetchProducts();
      setIsEditing(null);
      setEditedProduct({
        productId: '',
        productName: '',
        categoryId: '',
        description: '',
        price: '',
        productImageUrl: ''
      });
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm mới!", error);
    }
  };

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      <Button variant="primary" className="mb-3" onClick={handleCreateProduct}>
        Create Product
      </Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    type="text"
                    name="productName"
                    value={editedProduct.productName}
                    onChange={handleInputChange}
                  />
                ) : (
                  product.productName
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    as="select"
                    name="categoryId"
                    value={editedProduct.categoryId}
                    onChange={handleCategoryChange}
                  >
                    {categories.map((category) => (
                      <option key={category.categoryId} value={category.categoryId}>
                        {category.categoryName}
                      </option>
                    ))}
                  </Form.Control>
                ) : (
                  product.category?.categoryName
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    type="text"
                    name="description"
                    value={editedProduct.description}
                    onChange={handleInputChange}
                  />
                ) : (
                  product.description
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    type="number"
                    name="price"
                    value={editedProduct.price}
                    onChange={handleInputChange}
                  />
                ) : (
                  product.price
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <input
                    type="file"
                    onChange={handleImageChange}
                  />
                ) : (
                  <Image src={product.productImageUrl} alt="Product" width={50} />
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <>
                    <Button variant="success" onClick={handleSaveClick}>Save</Button>
                    <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
                  </>
                ) : (
                  <Button variant="warning" onClick={() => handleEditClick(product.productId)}>Edit</Button>
                )}
              </td>
            </tr>
          ))}
          {isEditing === 'new' && (
            <tr>
              <td>New</td>
              <td>
                <Form.Control
                  type="text"
                  name="productName"
                  value={editedProduct.productName}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <Form.Control
                  as="select"
                  name="categoryId"
                  value={editedProduct.categoryId}
                  onChange={handleCategoryChange}
                >
                  {categories.map((category) => (
                    <option key={category.categoryId} value={category.categoryId}>
                      {category.categoryName}
                    </option>
                  ))}
                </Form.Control>
              </td>
              <td>
                <Form.Control
                  type="text"
                  name="description"
                  value={editedProduct.description}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <Form.Control
                  type="number"
                  name="price"
                  value={editedProduct.price}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <input
                  type="file"
                  onChange={handleImageChange}
                />
              </td>
              <td>
                <Button variant="success" onClick={handleCreateSaveClick}>Save</Button>
                <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default ProductPage;
