import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const CategoryPage = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    // Kiểm tra nếu không có token hoặc vai trò không phải Admin User
    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login'); // Điều hướng về trang login nếu không có quyền truy cập
    } else {
      // Fetch categories nếu người dùng có quyền truy cập
      axios.get('http://localhost:5203/api/category', {
        headers: {
          Authorization: `Bearer ${token}`, // Đảm bảo thêm token vào header Authorization
        }
      })
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the categories!", error);
      });
    }
  }, [navigate]);

  return (
    <div>
      <h2>Manage Categories</h2>
      <Button variant="primary" onClick={() => alert('Create new category')}>Create Category</Button>
      <Table striped bordered hover className="mt-4">
        <thead>
          <tr>
            <th>Category ID</th>
            <th>Category Name</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.categoryId}>
              <td>{category.categoryId}</td>
              <td>{category.categoryName}</td>
              <td>{category.categoryDescription}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default CategoryPage;
