import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const CategoryPage = () => {
  const [categories, setCategories] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const [editedCategory, setEditedCategory] = useState({ categoryId: '', categoryName: '', categoryDescription: '' });
  const [error, setError] = useState('');  // Thêm state error
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login');
    } else {
      axios.get('http://localhost:5203/api/category', {
        headers: {
          Authorization: `Bearer ${token}`,
        }
      })
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the categories!", error);
      });
    }
  }, [navigate]);

  const handleEditClick = (category) => {
    setIsEditing(category.categoryId);
    setEditedCategory(category);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedCategory((prev) => ({
      ...prev,
      [name]: value
    }));
    // Xóa lỗi khi người dùng nhập vào trường Category Name
    if (name === 'categoryName') {
      setError('');
    }
  };

  const handleSaveClick = () => {
    // Kiểm tra nếu Category Name không có giá trị, hiển thị thông báo lỗi
    if (!editedCategory.categoryName) {
      setError('Category Name is required');
      return;
    }

    axios.put(`http://localhost:5203/api/category/${editedCategory.categoryId}`, editedCategory)
      .then(response => {
        setCategories(categories.map((category) =>
          category.categoryId === editedCategory.categoryId ? editedCategory : category
        ));
        setIsEditing(null);
      })
      .catch(error => {
        console.error("There was an error updating the category!", error);
      });
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
    setError('');  // Đặt lại lỗi khi hủy
  };

  const handleCreateCategory = () => {
    const newCategory = { categoryName: '', categoryDescription: '' };
    setIsEditing('new');
    setEditedCategory(newCategory);
    setError('');  // Đặt lại lỗi khi tạo mới
  };

  const handleCreateSaveClick = () => {
    // Kiểm tra nếu Category Name không có giá trị, hiển thị thông báo lỗi
    if (!editedCategory.categoryName) {
      setError('Category Name is required');
      return;
    }

    axios.post('http://localhost:5203/api/category', editedCategory)
      .then(response => {
        setCategories([...categories, response.data]);
        setIsEditing(null);
        setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
        setError('');  // Đặt lại lỗi khi lưu thành công
      })
      .catch(error => {
        console.error("There was an error creating the category!", error);
      });
  };

  return (
    <div>
      <h2>Manage Categories</h2>
      <Button variant="primary" onClick={handleCreateCategory}>Create Category</Button>
      
      {/* Hiển thị thông báo lỗi nếu có */}
      {error && <Alert variant="danger">{error}</Alert>}
      
      <Table striped bordered hover className="mt-4">
        <thead>
          <tr>
            <th>Category ID</th>
            <th>Category Name</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.categoryId}>
              <td>{category.categoryId}</td>
              <td>
                {isEditing === category.categoryId ? (
                  <Form.Control
                    type="text"
                    name="categoryName"
                    value={editedCategory.categoryName}
                    onChange={handleInputChange}
                    isInvalid={!editedCategory.categoryName && isEditing === category.categoryId}  // Thêm điều kiện lỗi
                  />
                ) : (
                  category.categoryName
                )}
              </td>
              <td>
                {isEditing === category.categoryId ? (
                  <Form.Control
                    type="text"
                    name="categoryDescription"
                    value={editedCategory.categoryDescription}
                    onChange={handleInputChange}
                  />
                ) : (
                  category.categoryDescription
                )}
              </td>
              <td>
                {isEditing === category.categoryId ? (
                  <>
                    <Button variant="success" onClick={handleSaveClick}>Save</Button>
                    <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
                  </>
                ) : (
                  <Button variant="warning" onClick={() => handleEditClick(category)}>Edit</Button>
                )}
              </td>
            </tr>
          ))}
          {isEditing === 'new' && (
            <tr>
              <td>New</td>
              <td>
                <Form.Control
                  type="text"
                  name="categoryName"
                  value={editedCategory.categoryName}
                  onChange={handleInputChange}
                  isInvalid={!editedCategory.categoryName}  // Thêm điều kiện lỗi
                />
              </td>
              <td>
                <Form.Control
                  type="text"
                  name="categoryDescription"
                  value={editedCategory.categoryDescription}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <Button variant="success" onClick={handleCreateSaveClick}>Save</Button>
                <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default CategoryPage;
