import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false); // Show modal flag
  const [newProduct, setNewProduct] = useState({
    productName: '',
    category: '',
    productDescription: '',
    productPrice: '',
    productImageUrl: '',
  });
  const [isSaveEnabled, setIsSaveEnabled] = useState(false); // Control Save button
  const navigate = useNavigate(); // useNavigate hook for redirecting

  // Check if the user is logged in and if the user is an admin
  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  // Fetch Data
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Handlers
  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, category: { categoryId: value } } : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSaveClick = async () => {
    const product = { ...newProduct, categoryId: newProduct.category };
    try {
      await axiosInstance.post('Product', product);
      fetchProducts(currentPage, pageSize);
      setShowCreateModal(false);
      setNewProduct({
        productName: '',
        category: '',
        productDescription: '',
        productPrice: '',
        productImageUrl: '',
      });
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setShowCreateModal(false);
    setNewProduct({
      productName: '',
      category: '',
      productDescription: '',
      productPrice: '',
      productImageUrl: '',
    });
  };

  // Enable Save button if Product Name and Category are not empty
  useEffect(() => {
    const { productName, category } = newProduct;
    setIsSaveEnabled(productName && category);
  }, [newProduct]);

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>{product.productName}</td>
                <td>{product.category?.categoryName || 'N/A'}</td>
                <td>{product.productDescription}</td>
                <td>{product.productPrice}</td>
                <td>
                  {product.productImageUrl && (
                    <Image
                      src={`http://localhost:5072${product.productImageUrl}`}
                      alt={product.productName}
                      width={50}
                    />
                  )}
                </td>
                <td>{product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}</td>
                <td>{product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    <Button variant="warning" onClick={() => handleEditClick(product.productId)}>
                      Edit
                    </Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Create Product Modal */}
      <Modal show={showCreateModal} onHide={handleCancelClick}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name <span className="text-danger">*</span></Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product name"
                value={newProduct.productName}
                onChange={(e) => setNewProduct({ ...newProduct, productName: e.target.value })}
              />
            </Form.Group>

            <Form.Group controlId="category">
              <Form.Label>Category <span className="text-danger">*</span></Form.Label>
              <Form.Control
                as="select"
                value={newProduct.category}
                onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="productDescription">
              <Form.Label>Product Description</Form.Label>
              <Form.Control
                as="textarea"
                placeholder="Enter product description"
                value={newProduct.productDescription}
                onChange={(e) => setNewProduct({ ...newProduct, productDescription: e.target.value })}
              />
            </Form.Group>

            <Form.Group controlId="productPrice">
              <Form.Label>Product Price</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter product price"
                value={newProduct.productPrice}
                onChange={(e) => setNewProduct({ ...newProduct, productPrice: e.target.value })}
              />
            </Form.Group>

            <Form.Group controlId="productImage">
              <Form.Label>Product Image</Form.Label>
              <Form.Control
                type="file"
                onChange={handleImageChange}
              />
              {selectedImage && <img src={selectedImage} alt="Selected Preview" width={100} />}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCancelClick}>
            Cancel
          </Button>
          <Button
            variant="primary"
            onClick={handleSaveClick}
            disabled={!isSaveEnabled}
          >
            Save Product
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
