import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // State to track if user is an admin
  const [showCreatePopup, setShowCreatePopup] = useState(false); // State for showing Create Product popup
  const [newProduct, setNewProduct] = useState({
    productName: '',
    categoryId: '',
    productDescription: '',
    productPrice: '',
    productImageUrl: ''
  });
  const navigate = useNavigate(); // useNavigate hook for redirecting

  // Check if the user is logged in and if the user is an admin
  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login'); // Redirect to login page if not logged in or not admin
    }
  }, [navigate]);

  // Fetch Data
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Handlers for Create Product Popup
  const handleCreateProductClick = () => {
    setShowCreatePopup(true); // Show popup
  };

  const handleCreateProductClose = () => {
    setShowCreatePopup(false); // Close popup
  };

  const handleCreateProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCreateProductSave = async () => {
    const product = { ...newProduct };

    try {
      await axiosInstance.post('Product', product); // Send POST request to create product
      fetchProducts(currentPage, pageSize); // Reload the products list
      setShowCreatePopup(false); // Close popup after saving
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm mới!", error);
    }
  };

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && ( // Only show Create Product button if logged in and is admin
        <Button variant="primary" className="mb-3" onClick={handleCreateProductClick}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>} {/* Only show Actions if logged in and admin */}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>{product.productName}</td>
                <td>{product.category?.categoryName || 'N/A'}</td>
                <td>{product.productDescription}</td>
                <td>{product.productPrice}</td>
                <td>
                  {product.productImageUrl && (
                    <Image
                      src={`http://localhost:5072${product.productImageUrl}`}
                      alt={product.productName}
                      width={50}
                    />
                  )}
                </td>
                <td>
                  {product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}
                </td>
                <td>
                  {product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}
                </td>
                {isLoggedIn && isAdmin && ( // Conditionally render the action buttons
                  <td>
                    <Button variant="warning">Edit</Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Create Product Modal */}
      <Modal show={showCreatePopup} onHide={handleCreateProductClose}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={newProduct.productName}
                onChange={handleCreateProductChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="categoryId">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleCreateProductChange}
                required
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="productDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="text"
                name="productDescription"
                value={newProduct.productDescription}
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group controlId="productPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={newProduct.productPrice}
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group controlId="productImage">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                name="productImage"
                onChange={(e) => setSelectedImage(URL.createObjectURL(e.target.files[0]))}
              />
              {selectedImage && <Image src={selectedImage} width={100} />}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCreateProductClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCreateProductSave}>
            Save Product
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
