  //   if (file) {
  //     const imagePreview = URL.createObjectURL(file);
  //     setUploadedImage(imagePreview); // Hiển thị ảnh tạm

  //     setSelectedImage(URL.createObjectURL(file));
  //     const formData = new FormData();
  //     formData.append("image", file);

