import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const CategoryPage = () => {
  const [categories, setCategories] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const [editedCategory, setEditedCategory] = useState({ categoryId: '', categoryName: '', categoryDescription: '' });
  const [showModal, setShowModal] = useState(false); // Modal state
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login');
    } else {
      axios.get('http://localhost:5203/api/category', {
        headers: {
          Authorization: `Bearer ${token}`,
        }
      })
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the categories!", error);
      });
    }
  }, [navigate]);

  const handleEditClick = (category) => {
    setIsEditing(category.categoryId);
    setEditedCategory(category);
    setShowModal(true); // Show modal when editing
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedCategory((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSaveClick = () => {
    axios.put(`http://localhost:5203/api/category/${editedCategory.categoryId}`, editedCategory)
      .then(response => {
        setCategories(categories.map((category) =>
          category.categoryId === editedCategory.categoryId ? editedCategory : category
        ));
        setShowModal(false); // Close modal after save
        setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
      })
      .catch(error => {
        console.error("There was an error updating the category!", error);
      });
  };

  const handleCancelClick = () => {
    setShowModal(false); // Close modal when cancel
    setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
  };

  const handleCreateCategory = () => {
    setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
    setIsEditing('new');
    setShowModal(true); // Show modal when creating new category
  };

  const handleCreateSaveClick = () => {
    axios.post('http://localhost:5203/api/category', editedCategory)
      .then(response => {
        setCategories([...categories, response.data]);
        setShowModal(false); // Close modal after save
        setEditedCategory({ categoryId: '', categoryName: '', categoryDescription: '' });
      })
      .catch(error => {
        console.error("There was an error creating the category!", error);
      });
  };

  return (
    <div>
      <h2>Manage Categories</h2>
      <Button variant="primary" onClick={handleCreateCategory}>Create Category</Button>
      <Table striped bordered hover className="mt-4">
        <thead>
          <tr>
            <th>Category ID</th>
            <th>Category Name</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.categoryId}>
              <td>{category.categoryId}</td>
              <td>{category.categoryName}</td>
              <td>{category.categoryDescription}</td>
              <td>
                <Button variant="warning" onClick={() => handleEditClick(category)}>Edit</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal for Create/Edit Category */}
      <Modal show={showModal} onHide={handleCancelClick}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing === 'new' ? 'Create Category' : 'Edit Category'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="categoryName">
              <Form.Label>Category Name</Form.Label>
              <Form.Control
                type="text"
                name="categoryName"
                value={editedCategory.categoryName}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="categoryDescription" className="mt-2">
              <Form.Label>Category Description</Form.Label>
              <Form.Control
                type="text"
                name="categoryDescription"
                value={editedCategory.categoryDescription}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCancelClick}>Cancel</Button>
          {isEditing === 'new' ? (
            <Button variant="success" onClick={handleCreateSaveClick}>Save</Button>
          ) : (
            <Button variant="success" onClick={handleSaveClick}>Save</Button>
          )}
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default CategoryPage;
