import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

// Setup Axios Instance
const getAuthToken = () => localStorage.getItem('token');
const getUserRole = () => localStorage.getItem('roles'); // Get user role from localStorage

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      const token = getAuthToken();
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // State to track if user is an admin
  const navigate = useNavigate(); // useNavigate hook for redirecting

  

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && ( // Only show Create Product button if logged in and is admin
        <Button variant="primary" className="mb-3" onClick={() => alert('Create new product')}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>} {/* Only show Actions if logged in and admin */}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ''}
                      onChange={(e) => handleCategoryChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category?.categoryName || 'N/A'
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productPrice
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <input
                      type="file"
                      onChange={(e) => handleImageChange(e, product.productId)}
                      style={{ display: 'none' }}
                      id={`upload_${product.productId}`}
                    />
                  ) : (
                    product.productImageUrl && (
                      <Image
                        src={`http://localhost:5072${product.productImageUrl}`}
                        alt={product.productName}
                        width={50}
                      />
                    )
                  )}
                </td>
                <td>
                  {product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}
                </td>
                <td>
                  {product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}
                </td>
                {isLoggedIn && isAdmin && ( // Conditionally render the action buttons
                  <td>
                    {isEditing === product.productId ? (
                      <>
                        <Button variant="success" onClick={() => handleSaveClick(product.productId)}>
                          Save
                        </Button>
                        <Button variant="secondary" onClick={handleCancelClick}>
                          Cancel
                        </Button>
                      </>
                    ) : (
                      <>
                        <Button variant="warning" onClick={() => handleEditClick(product.productId)}>
                          Edit
                        </Button>
                      </>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
