import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [validationError, setValidationError] = useState('');
  const navigate = useNavigate();  // Khai báo useNavigate thay vì useHistory

  const handleLogin = async (e) => {
    e.preventDefault();

    // Reset các thông báo lỗi trước khi kiểm tra
    setError('');
    setValidationError('');

    // Kiểm tra validation chỉ khi nhấn Login
    if (!username || !password) {
      setValidationError('Username & Password are required!');
      return;  // Không gửi yêu cầu nếu có trường bị trống
    }

    try {
      const response = await axios.post('http://localhost:5203/api/account/login', {
        username,
        password,
        rememberMe: true,
      });

      if (response.data.token) {
        // Lưu token vào localStorage
        localStorage.setItem('token', response.data.token);

        // Kiểm tra vai trò từ response
        const roles = response.data.roles;

        if (roles && roles.includes("Admin User")) {
          setIsLoggedIn(true);  // Cập nhật trạng thái đăng nhập
          navigate('/');  // Chuyển hướng về trang chủ hoặc trang của Admin
        } else {
          setError('Access denied !');
          localStorage.removeItem('token');  // Xóa token nếu không phải Admin User
        }
      }
    } catch (err) {
      setError('Invalid credentials, please try again!');
    }
  };

  return (
    <div style={{ width: '300px', margin: 'auto', padding: '20px' }}>
      <h2>Login</h2>

      {/* Hiển thị lỗi validation nếu có */}
      {validationError && <div style={{ color: 'red', marginBottom: '10px' }}>{validationError}</div>}

      {/* Hiển thị lỗi đăng nhập nếu có */}
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}

      <Form onSubmit={handleLogin}>
        <Form.Group>
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>

        <Button 
          variant="primary" 
          type="submit" 
          style={{ marginTop: '15px' }}  
        >
          Login
        </Button>
      </Form>
    </div>
  );
};

export default LoginPage;
