import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false); // Modal state for create product
  const [showEditModal, setShowEditModal] = useState(false); // Modal state for edit product
  const [newProduct, setNewProduct] = useState({
    productName: '',
    productDescription: '',
    productPrice: 0,
    categoryId: '',
    productImageUrl: '',
  });
  const [editedProduct, setEditedProduct] = useState({
    productId: '',
    productName: '',
    productDescription: '',
    productPrice: 0,
    categoryId: '',
    productImageUrl: '',
  });
  const [selectedImage, setSelectedImage] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handleCreateProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditProductChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setEditedProduct((prev) => ({
            ...prev,
            productImageUrl: response.data.imageUrl,
          }));
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSaveNewProduct = async () => {
    try {
      const response = await axiosInstance.post('Product', newProduct);
      setShowCreateModal(false); // Close modal after saving
      fetchProducts(currentPage, pageSize);
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm!", error);
    }
  };

  const handleSaveEditedProduct = async () => {
    try {
      const response = await axiosInstance.put(`Product/${editedProduct.productId}`, editedProduct);
      setShowEditModal(false); // Close modal after saving
      fetchProducts(currentPage, pageSize);
    } catch (error) {
      console.error("Có lỗi khi chỉnh sửa sản phẩm!", error);
    }
  };

  const handleEditProduct = (product) => {
    setEditedProduct({
      productId: product.productId,
      productName: product.productName,
      productDescription: product.productDescription,
      productPrice: product.productPrice,
      categoryId: product.categoryId,
      productImageUrl: product.productImageUrl,
    });
    setShowEditModal(true);
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Create Product
        </Button>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>{product.productName}</td>
                <td>{product.category?.categoryName || 'N/A'}</td>
                <td>{product.productDescription}</td>
                <td>{product.productPrice}</td>
                <td>
                  {product.productImageUrl && (
                    <Image
                      src={`http://localhost:5072${product.productImageUrl}`}
                      alt={product.productName}
                      width={50}
                    />
                  )}
                </td>
                <td>{product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}</td>
                <td>{product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    <Button variant="warning" onClick={() => handleEditProduct(product)}>Edit</Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>

      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Create Product Modal */}
      <Modal show={showCreateModal} onHide={() => setShowCreateModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={newProduct.productName}
                onChange={handleCreateProductChange}
                placeholder="Enter product name"
              />
            </Form.Group>

            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleCreateProductChange}
              >
                <option value="">Select category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="formProductDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                name="productDescription"
                value={newProduct.productDescription}
                onChange={handleCreateProductChange}
                placeholder="Enter product description"
              />
            </Form.Group>

            <Form.Group controlId="formProductPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={newProduct.productPrice}
                onChange={handleCreateProductChange}
                placeholder="Enter product price"
              />
            </Form.Group>

            <Form.Group controlId="formProductImage">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                onChange={handleImageChange}
              />
              {selectedImage && <Image src={selectedImage} alt="Selected preview" width={100} />}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowCreateModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveNewProduct}>Save</Button>
        </Modal.Footer>
      </Modal>

      {/* Edit Product Modal */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={editedProduct.productName}
                onChange={handleEditProductChange}
                placeholder="Enter product name"
              />
            </Form.Group>

            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={editedProduct.categoryId}
                onChange={handleEditProductChange}
              >
                <option value="">Select category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="formProductDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                name="productDescription"
                value={editedProduct.productDescription}
                onChange={handleEditProductChange}
                placeholder="Enter product description"
              />
            </Form.Group>

            <Form.Group controlId="formProductPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={editedProduct.productPrice}
                onChange={handleEditProductChange}
                placeholder="Enter product price"
              />
            </Form.Group>

            <Form.Group controlId="formProductImage">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                onChange={handleImageChange}
              />
              {selectedImage && <Image src={selectedImage} alt="Selected preview" width={100} />}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveEditedProduct}>Save</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
