import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table } from 'react-bootstrap';

const CategoryPage = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch categories from API
    axios.get('http://localhost:5203/api/category', {
      headers: {
        // Đảm bảo bạn không quên thêm bất kỳ header nào nếu cần thiết (ví dụ Authorization)
      }
    })
    .then(response => {
      setCategories(response.data);
    })
    .catch(error => {
      console.error("There was an error fetching the categories!", error);
    });
  }, []);

  return (
    <div>
      <h2>Manage Categories</h2>
      <Button variant="primary" onClick={() => alert('Create new category')}>Create Category</Button>
      <Table striped bordered hover className="mt-4">
        <thead>
          <tr>
            <th>Category ID</th>
            <th>Category Name</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.categoryId}>
              <td>{category.categoryId}</td>
              <td>{category.categoryName}</td>
              <td>{category.categoryDescription}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default CategoryPage;
