import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

// Axios Instance Setup
const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Interceptors for Axios
axiosInstance.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

axiosInstance.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      window.dispatchEvent(new Event('tokenExpired'));
    }
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  // Check user login and roles
  useEffect(() => {
    const token = localStorage.getItem('token');
    const roles = JSON.parse(localStorage.getItem('roles'));

    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }

    // Token expiration handling
    const handleTokenExpired = () => setShowPopup(true);
    window.addEventListener('tokenExpired', handleTokenExpired);

    return () => {
      window.removeEventListener('tokenExpired', handleTokenExpired);
    };
  }, [navigate]);

  // Fetch categories and products
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      setProducts(response.data.items || []);
      setTotalPages(response.data.totalPages || 1);
    } catch (error) {
      console.error('Error fetching products:', error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Handlers
  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, category: { categoryId: value } } : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append('image', file);

      axiosInstance
        .post('Product/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => console.error('Error uploading image:', error));
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find((p) => p.productId === productId);
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId,
    };

    try {
      await axiosInstance.put(`Product/${productId}`, updatedProduct);
      fetchProducts(currentPage, pageSize);
      setIsEditing(null);
      setSelectedImage(null);
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
    navigate('/login');
  };

  return (
    <div>
      <h2>Manage Products</h2>
      <Modal show={showPopup} onHide={handlePopupClose}>
        <Modal.Header closeButton>
          <Modal.Title>Session Expired</Modal.Title>
        </Modal.Header>
        <Modal.Body>Your session has expired. Please log in again.</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handlePopupClose}>
            Go to Login
          </Button>
        </Modal.Footer>
      </Modal>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Image</th>
            {isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    type="text"
                    name="productName"
                    value={product.productName}
                    onChange={(e) => handleInputChange(e, product.productId)}
                  />
                ) : (
                  product.productName
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Select
                    value={product.category?.categoryId || ''}
                    onChange={(e) => handleCategoryChange(e, product.productId)}
                  >
                    <option value="">Select Category</option>
                    {categories.map((cat) => (
                      <option key={cat.categoryId} value={cat.categoryId}>
                        {cat.categoryName}
                      </option>
                    ))}
                  </Form.Select>
                ) : (
                  product.category?.categoryName || 'No Category'
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <Form.Control
                    type="number"
                    name="price"
                    value={product.price}
                    onChange={(e) => handleInputChange(e, product.productId)}
                  />
                ) : (
                  `$${product.price}`
                )}
              </td>
              <td>
                <Image
                  src={product.productImageUrl || 'placeholder.jpg'}
                  thumbnail
                  width={50}
                />
                {isEditing === product.productId && (
                  <Form.Control
                    type="file"
                    onChange={(e) => handleImageChange(e, product.productId)}
                  />
                )}
              </td>
              {isAdmin && (
                <td>
                  {isEditing === product.productId ? (
                    <>
                      <Button
                        variant="success"
                        onClick={() => handleSaveClick(product.productId)}
                      >
                        Save
                      </Button>{' '}
                      <Button variant="secondary" onClick={handleCancelClick}>
                        Cancel
                      </Button>
                    </>
                  ) : (
                    <Button
                      variant="warning"
                      onClick={() => handleEditClick(product.productId)}
                    >
                      Edit
                    </Button>
                  )}
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
