import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false); // State to control modal visibility
  const [newProduct, setNewProduct] = useState({
    productName: '',
    categoryId: '',
    productDescription: '',
    productPrice: '',
    productImageUrl: '',
  });
  const [isFormValid, setIsFormValid] = useState(false); // State to enable/disable Save button
  const [productToEdit, setProductToEdit] = useState(null); // State for editing

  const navigate = useNavigate();

  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  const handleCreateProductClick = () => {
    setProductToEdit(null); // Clear any previous editing data
    setShowCreateModal(true); // Show the modal
  };

  const handleCloseModal = () => {
    setShowCreateModal(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCategoryChange = (e) => {
    const { value } = e.target;
    setNewProduct((prev) => ({
      ...prev,
      categoryId: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append('image', file);

      axiosInstance
        .post('Product/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        })
        .then((response) => {
          setNewProduct((prev) => ({
            ...prev,
            productImageUrl: response.data.imageUrl,
          }));
        })
        .catch((error) => {
          console.error('Có lỗi khi upload hình ảnh!', error);
        });
    }
  };

  const handleSaveProduct = async () => {
    try {
      if (productToEdit) {
        // Update existing product
        await axiosInstance.put(`Product/${productToEdit.productId}`, newProduct);
      } else {
        // Create new product
        await axiosInstance.post('Product', newProduct);
      }
      fetchProducts(currentPage, pageSize);
      handleCloseModal();
    } catch (error) {
      console.error('Có lỗi khi lưu sản phẩm!', error);
    }
  };

  const handleEditProduct = (product) => {
    setProductToEdit(product); // Set the product to edit
    setNewProduct({
      productName: product.productName,
      categoryId: product.categoryId,
      productDescription: product.productDescription,
      productPrice: product.productPrice,
      productImageUrl: product.productImageUrl,
    });
    setShowCreateModal(true); // Show the modal for editing
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axiosInstance.delete(`Product/${productId}`);
      fetchProducts(currentPage, pageSize); // Re-fetch products
    } catch (error) {
      console.error('Có lỗi khi xóa sản phẩm!', error);
    }
  };

  useEffect(() => {
    // Enable Save button only if productName and categoryId are provided
    setIsFormValid(newProduct.productName && newProduct.categoryId);
  }, [newProduct]);

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={handleCreateProductClick}>
          Create Product
        </Button>
      )}

      {/* Modal for creating or editing product */}
      <Modal show={showCreateModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{productToEdit ? 'Edit Product' : 'Create Product'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name <span style={{ color: 'red' }}>*</span></Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={newProduct.productName}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group controlId="categoryId">
              <Form.Label>Category <span style={{ color: 'red' }}>*</span></Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleCategoryChange}
                required
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="productDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="text"
                name="productDescription"
                value={newProduct.productDescription}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="productPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={newProduct.productPrice}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="productImage">
              <Form.Label>Image</Form.Label>
              <input
                type="file"
                onChange={handleImageChange}
                style={{ display: 'none' }}
                id="upload_image"
              />
              <Button variant="secondary" onClick={() => document.getElementById('upload_image').click()}>
                Choose Image
              </Button>
              {selectedImage && <Image src={selectedImage} width={50} />}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={handleSaveProduct}
            disabled={!isFormValid}
          >
            {productToEdit ? 'Save Changes' : 'Create Product'}
          </Button>
        </Modal.Footer>
      </Modal>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Description</th>
            <th>Image</th>
            <th>Updated Date</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productName}</td>
                <td>{product.categoryName}</td>
                <td>{product.productPrice}</td>
                <td>{product.productDescription}</td>
                <td>
                  <img
                    src={product.productImageUrl || 'default-image-url'}
                    alt={product.productName}
                    width={50}
                  />
                </td>
                <td>{new Date(product.updatedDate).toLocaleDateString()}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    <Button variant="warning" onClick={() => handleEditProduct(product)}>
                      Edit
                    </Button>{' '}
                    <Button variant="danger" onClick={() => handleDeleteProduct(product.productId)}>
                      Delete
                    </Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8" className="text-center">
                No products available.
              </td>
            </tr>
          )}
        </tbody>
      </Table>

      <Pagination>
        {Array.from({ length: totalPages }).map((_, idx) => (
          <Pagination.Item
            key={idx + 1}
            active={idx + 1 === currentPage}
            onClick={() => setCurrentPage(idx + 1)}
          >
            {idx + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
