import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Table, Pagination, Image, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./ProductPage.css"; // Import file CSS tùy chỉnh

const token = localStorage.getItem("token");
const roles = JSON.parse(localStorage.getItem("roles"));

const axiosInstance = axios.create({
  baseURL: "http://localhost:5203/api/",
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (["post", "put"].includes(config.method)) {
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => Promise.reject(error)
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (token && roles && roles.includes("Admin User")) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate("/login");
    }
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get("Category");
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get("Product/pagination", {
        params: { pageNumber, pageSize },
      });

      if (response.data && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Error fetching products!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId
          ? { ...product, category: { categoryId: value } }
          : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => {
          console.error("Error uploading image!", error);
        });
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find((p) => p.productId === productId);
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId,
    };

    try {
      await axiosInstance.put(`Product/${productId}`, updatedProduct);
      fetchProducts(currentPage, pageSize);
      setIsEditing(null);
      setSelectedImage(null);
    } catch (error) {
      console.error("Error updating product!", error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null);
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => alert("Create new product")}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ""}
                      onChange={(e) => handleCategoryChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category?.categoryName || "N/A"
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productPrice
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <>
                      <input
                        type="file"
                        onChange={(e) => handleImageChange(e, product.productId)}
                        id={`upload_${product.productId}`}
                      />
                      <a href={`http://localhost:5072${product.productImageUrl}`} target="_blank" rel="noopener noreferrer">
                        Current Image
                      </a>
                    </>
                  ) : (
                    product.productImageUrl && (
                      <Image
                        src={`http://localhost:5072${product.productImageUrl}`}
                        alt={product.productName}
                        width={50}
                      />
                    )
                  )}
                </td>
                <td>
                  {product.createdDate ? new Date(product.createdDate).toLocaleDateString() : "N/A"}
                </td>
                <td>
                  {product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : "N/A"}
                </td>
                {isLoggedIn && isAdmin && (
                  <td>
                    {isEditing === product.productId ? (
                      <>
                        <Button
                          variant="success"
                          className="action-button"
                          onClick={() => handleSaveClick(product.productId)}
                        >
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          className="action-button ml-2"
                          onClick={handleCancelClick}
                        >
                          Cancel
                        </Button>
                      </>
                    ) : (
                      <Button
                        variant="warning"
                        className="action-button"
                        onClick={() => handleEditClick(product.productId)}
                      >
                        Edit
                      </Button>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={9} className="text-center">
                No products found.
              </td>
            </tr>
          )}
        </tbody>
      </Table>
      {totalPages > 1 && (
        <Pagination>
          {Array.from({ length: totalPages }, (_, index) => (
            <Pagination.Item
              key={index}
              active={index + 1 === currentPage}
              onClick={() => setCurrentPage(index + 1)}
            >
              {index + 1}
            </Pagination.Item>
          ))}
        </Pagination>
      )}
    </div>
  );
};

export default ProductPage;
