import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CustomerPage = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const roles = JSON.parse(localStorage.getItem('roles'));

  useEffect(() => {
    // Kiểm tra quyền truy cập
    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login'); // Nếu không phải Admin User, chuyển hướng về trang login
    } else {
      // Lấy danh sách users
      axios
        .get('http://localhost:5203/api/Account/basic-users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setCustomers(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError('There was an error fetching the users!');
          setLoading(false);
        });
    }
  }, [token, roles, navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Customer Users</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
        {customers.map((customer) => (
          <div
            key={customer.userName}
            style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '10px',
              backgroundColor: '#fff',
              boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              textAlign: 'center',
            }}
          >
            <img
              src="https://via.placeholder.com/100" // Placeholder image (có thể thay đổi)
              alt={customer.userName}
              style={{
                borderRadius: '50%',
                width: '100px',
                height: '100px',
                objectFit: 'cover',
                marginBottom: '10px',
              }}
            />
            <div>
              <h3>{customer.userName}</h3>
              <p>Email: {customer.email}</p>
              <p>Phone: {customer.phoneNumber}</p>
              <p>Role: {customer.roleName}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomerPage;
