import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap'; // S·ª≠ d·ª•ng c√°c th√†nh ph·∫ßn c·ªßa react-bootstrap

const HomePage = () => {
  // State ƒë·ªÉ l∆∞u th√¥ng b√°o c·∫ßn hi·ªÉn th·ªã
  const [message, setMessage] = useState(
    'Please select the login button at the top right to sign in and start working.'
  );

  // Ki·ªÉm tra token v√† role khi component ƒë∆∞·ª£c mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');

    if (!token || !roles || !roles.includes('Admin User')) {
      setMessage(
        'After logging in, please select the menu options displayed such as View Customers, Manage Categories, and Manage Products to start working.'
      );
    }
  }, []); // Ch·∫°y m·ªôt l·∫ßn khi component ƒë∆∞·ª£c render l·∫ßn ƒë·∫ßu ti√™n

  return (
    <Container>
      {/* Ti√™u ƒë·ªÅ ch√≠nh */}
      <h2 className="text-center my-4">Welcome to Ecommerce Admin</h2>

      {/* M√¥ t·∫£ ng·∫Øn g·ªçn v·ªÅ trang */}
      <p className="text-center">
        This is the admin dashboard where you can manage various aspects of your ecommerce platform. 
        To access and perform administrative tasks such as managing products, categories, and customers, 
        you must have the appropriate user role (Admin User). If you do not have access, please contact your system administrator.
      </p>

      {/* C√°c th·∫ª qu·∫£n l√Ω */}
      <Row className="text-center mt-4">
        {/* Card for View Customers */}
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <div style={{ fontSize: '40px', color: '#007bff' }}>üë•</div>
              <Card.Title className="mt-3">Manage Customers</Card.Title>
              <Card.Text>
                View customer accounts and details.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>

        {/* Card for Manage Categories */}
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <div style={{ fontSize: '40px', color: '#28a745' }}>üì¶</div>
              <Card.Title className="mt-3">Manage Categories</Card.Title>
              <Card.Text>
              Manage category inventory and details.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>

        {/* Card for Manage Products */}
        <Col md={4} className="mb-4">
          <Card>
            <Card.Body>
              <div style={{ fontSize: '40px', color: '#ffc107' }}>‚öôÔ∏è</div>
              <Card.Title className="mt-3">Manage Products</Card.Title>
              <Card.Text>
                Manage product inventory and details.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* L·ªùi nh·∫Øc v·ªÅ n√∫t login */}
      <p className="text-center mt-5">
        <strong>{message}</strong>
      </p>
    </Container>
  );
};

export default HomePage;
