import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Container, Row, Col, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';  // Sử dụng useNavigate thay vì useHistory

const LoginPage = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();  // Khai báo useNavigate thay vì useHistory

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5203/api/account/login', {
        username,
        password,
        rememberMe: true,
      });

      if (response.data.token) {
        // Lưu token vào localStorage
        localStorage.setItem('token', response.data.token);
        setIsLoggedIn(true);  // Cập nhật trạng thái đăng nhập
        navigate('/');  // Chuyển hướng về trang chủ (hoặc sản phẩm)
      }
    } catch (err) {
      setError('Login failed, please check your credentials.');
    }
  };

  return (
    <Container
      fluid
      style={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#f4f7fc', // Màu nền trang login
      }}
    >
      <Row className="justify-content-center">
        <Col xs={12} sm={8} md={6} lg={4}>
          <div
            className="login-form-container"
            style={{
              padding: '30px',
              border: '1px solid #ddd',
              borderRadius: '10px',
              backgroundColor: '#fff',
              boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)', // Thêm bóng đổ cho form
            }}
          >
            <h2 className="text-center mb-4">Login</h2>
            {error && <Alert variant="danger">{error}</Alert>}

            <Form onSubmit={handleLogin}>
              <Form.Group className="mb-3" controlId="username">
                <Form.Label>Username</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  style={{ borderRadius: '5px' }} // Bo góc cho textbox
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  style={{ borderRadius: '5px' }} // Bo góc cho textbox
                />
              </Form.Group>

              <Button
                variant="primary"
                type="submit"
                block
                style={{
                  padding: '10px 0',
                  fontSize: '16px',
                  borderRadius: '5px',
                  marginTop: '10px',
                }}
              >
                Login
              </Button>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default LoginPage;
