const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    categoryId: '',
    description: '',
    price: '',
    image: null,
  });

  const navigate = useNavigate();
  const isAdmin = token && roles?.includes('Admin User');

  useEffect(() => {
    if (!isAdmin) {
      navigate('/login');
    }
  }, [navigate, isAdmin]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      setProducts(response.data.items || []);
      setTotalPages(response.data.totalPages || 1);
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handleCreateProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('categoryId', newProduct.categoryId);
      formData.append('description', newProduct.description);
      formData.append('price', newProduct.price);
      if (newProduct.image) {
        formData.append('image', newProduct.image);
      }

      await axiosInstance.post('Product', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      fetchProducts(currentPage, pageSize);
      setShowCreateModal(false);
      setNewProduct({
        name: '',
        categoryId: '',
        description: '',
        price: '',
        image: null,
      });
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm!", error);
    }
  };

  const handleEditProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('categoryId', newProduct.categoryId);
      formData.append('description', newProduct.description);
      formData.append('price', newProduct.price);
      if (newProduct.image) {
        formData.append('image', newProduct.image);
      }

      await axiosInstance.put(`Product/${currentProduct.productId}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      fetchProducts(currentPage, pageSize);
      setShowEditModal(false);
      setNewProduct({
        name: '',
        categoryId: '',
        description: '',
        price: '',
        image: null,
      });
    } catch (error) {
      console.error("Có lỗi khi chỉnh sửa sản phẩm!", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setNewProduct((prev) => ({ ...prev, image: file }));
  };

  const handleCloseCreateModal = () => {
    setShowCreateModal(false);
    setNewProduct({
      name: '',
      categoryId: '',
      description: '',
      price: '',
      image: null,
    });
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
    setCurrentProduct(null);
    setNewProduct({
      name: '',
      categoryId: '',
      description: '',
      price: '',
      image: null,
    });
  };

  const handleEditClick = (product) => {
    setCurrentProduct(product);
    setNewProduct({
      name: product.productName,
      categoryId: product.category?.categoryId || '',
      description: product.productDescription,
      price: product.productPrice,
      image: null,
    });
    setShowEditModal(true);
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Created Date</th>
            <th>Updated Date</th>
            {isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>{product.productName}</td>
                <td>{product.category?.categoryName || 'N/A'}</td>
                <td>{product.productDescription}</td>
                <td>{product.productPrice}</td>
                <td>
                  {product.productImageUrl && (
                    <Image
                      src={`http://localhost:5072${product.productImageUrl}`}
                      alt={product.productName}
                      width={50}
                    />
                  )}
                </td>
                <td>
                  {product.createdDate
                    ? new Date(product.createdDate).toLocaleDateString()
                    : 'N/A'}
                </td>
                <td>
                  {product.updatedDate
                    ? new Date(product.updatedDate).toLocaleDateString()
                    : 'N/A'}
                </td>
                {isAdmin && (
                  <td>
                    <Button variant="warning" onClick={() => handleEditClick(product)}>
                      Edit
                    </Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products available.</td>
            </tr>
          )}
        </tbody>
      </Table>
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>

      {/* Modal for Creating Product */}
      <Modal show={showCreateModal} onHide={handleCloseCreateModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={newProduct.name}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleInputChange}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={newProduct.description}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={newProduct.price}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Image</Form.Label>
              <Form.Control type="file" onChange={handleImageChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseCreateModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCreateProduct}>
            Create
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal for Editing Product */}
      <Modal show={showEditModal} onHide={handleCloseEditModal}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={newProduct.name}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleInputChange}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={newProduct.description}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={newProduct.price}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Image</Form.Label>
              <Form.Control type="file" onChange={handleImageChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEditModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleEditProduct}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
