import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CustomerPage = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const roles = JSON.parse(localStorage.getItem('roles'));

  useEffect(() => {
    // Kiểm tra quyền truy cập
    if (!token || !roles || !roles.includes('Admin User')) {
      navigate('/login'); // Nếu không phải Admin User, chuyển hướng về trang login
    } else {
      // Lấy danh sách users
      axios
        .get('http://localhost:5203/api/Account/basic-users', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setCustomers(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError('There was an error fetching the users !');
          setLoading(false);
        });
    }
  }, [token, roles, navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // CSS inline
  const customerPageStyle = {
    padding: '20px',
    backgroundColor: '#f9f9f9',
  };

  const headerStyle = {
    textAlign: 'center',
    marginBottom: '20px',
    color: '#333',
  };

  const customerGridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
    justifyItems: 'center',
  };

  const customerCardStyle = {
    background: '#fff',
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '20px',
    textAlign: 'center',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    width: '100%',
    maxWidth: '300px',
  };

  const customerImageStyle = {
    borderRadius: '50%',
    width: '100px',
    height: '100px',
    objectFit: 'cover',
    marginBottom: '15px',
  };

  const customerInfoStyle = {
    fontSize: '14px',
    color: '#666',
  };

  return (
    <div style={customerPageStyle}>
      <h1 style={headerStyle}>Customer Users</h1>
      <div style={customerGridStyle}>
        {customers.map((customer) => (
          <div style={customerCardStyle} key={customer.userName}>
            <img
              src="https://via.placeholder.com/100" // Placeholder image (có thể thay đổi)
              alt={customer.userName}
              style={customerImageStyle}
            />
            <div style={customerInfoStyle}>
              <h3>{customer.userName}</h3>
              <p>Email: {customer.email}</p>
              <p>Phone: {customer.phoneNumber}</p>
              <p>Role: {customer.roleName}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomerPage;
