import React, { useState, useEffect } from 'react';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';
import { Container, Navbar, Nav, Button, Row, Col } from 'react-bootstrap';
import CategoryPage from './pages/CategoryPage';
import ProductPage from './pages/ProductPage';
import CustomerPage from './pages/CustomerPage';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();  // Hook để điều hướng

  // Kiểm tra trạng thái đăng nhập khi trang web load
  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');  // Xóa token khỏi localStorage
    // Xóa cookies nếu cần
    document.cookie = 'token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    setIsLoggedIn(false);  // Cập nhật trạng thái đăng nhập
    navigate('/login');  // Điều hướng về trang Login
  };

  return (
    <div>
      {/* Navbar với các menu và nút login/logout */}
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="/" className="ecommerce-brand">Ecommerce Admin</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link as={Link} to="/" className="nav-link">Home</Nav.Link>
              <Nav.Link as={Link} to="/customers" className="nav-link">View Customers</Nav.Link>
              <Nav.Link as={Link} to="/categories" className="nav-link">Manage Categories</Nav.Link>
              <Nav.Link as={Link} to="/products" className="nav-link">Manage Products</Nav.Link>
            </Nav>
            <Nav>
              {/* Nút Login hoặc Logout */}
              {isLoggedIn ? (
                <Button variant="danger" onClick={handleLogout}>Logout</Button>
              ) : (
                <Link to="/login">
                  <Button variant="primary">Login</Button>
                </Link>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Routes */}
      <Container className="mt-4">
        <Row>
          <Col>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/customers" element={<CustomerPage />} />
              <Route path="/categories" element={<CategoryPage />} />
              <Route path="/products" element={<ProductPage isLoggedIn={isLoggedIn} />} />
              <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
            </Routes>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
