import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  // Hàm định dạng ngày tháng
  const formatDate = (date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    const seconds = String(d.getSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  };

  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });
      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, category: { categoryId: value } } : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find((p) => p.productId === productId);
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId,
    };

    try {
      await axiosInstance.put(`Product/${productId}`, updatedProduct);
      fetchProducts(currentPage, pageSize);
      setIsEditing(null);
      setSelectedImage(null);
    } catch (error) {
      console.error("Có lỗi khi cập nhật sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null);
  };

  const totalPrice = products.reduce((sum, product) => sum + (product.productPrice || 0), 0);

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => alert('Create new product')}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ''}
                      onChange={(e) => handleCategoryChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category?.categoryName || 'N/A'
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productPrice
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <div>
                      <a
                        href="#"
                        onClick={(e) => {
                          e.preventDefault();
                          document.getElementById(`upload_${product.productId}`).click();
                        }}
                      >
                        Upload Image
                      </a>
                      <input
                        type="file"
                        id={`upload_${product.productId}`}
                        style={{ display: 'none' }}
                        onChange={(e) => handleImageChange(e, product.productId)}
                      />
                    </div>
                  ) : (
                    product.productImageUrl && <Image src={product.productImageUrl} alt={product.productName} thumbnail width={50} />
                  )}
                </td>
                <td>{formatDate(product.createdDate)}</td>
                <td>{formatDate(product.updatedDate)}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    {isEditing === product.productId ? (
                      <>
                        <Button
                          variant="success"
                          onClick={() => handleSaveClick(product.productId)}
                        >
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          onClick={handleCancelClick}
                        >
                          Cancel
                        </Button>
                      </>
                    ) : (
                      <Button
                        variant="warning"
                        onClick={() => handleEditClick(product.productId)}
                      >
                        Edit
                      </Button>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products found.</td>
            </tr>
          )}
        </tbody>
      </Table>
      <Pagination>
        <Pagination.Prev onClick={() => setCurrentPage(Math.max(currentPage - 1, 1))} />
        <Pagination.Item>{currentPage}</Pagination.Item>
        <Pagination.Next onClick={() => setCurrentPage(Math.min(currentPage + 1, totalPages))} />
      </Pagination>
      <h4>Total Price: {totalPrice}</h4>
    </div>
  );
};

export default ProductPage;
