import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Image, Pagination } from 'react-bootstrap';

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    productDescription: '',
    productPrice: '',
    categoryId: '',
    productImage: null,
  });
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  
  // Khai báo pageSize (số lượng sản phẩm mỗi trang)
  const [pageSize, setPageSize] = useState(10); // Ví dụ: 10 sản phẩm mỗi trang

  useEffect(() => {
    // Logic lấy dữ liệu sản phẩm và thể loại từ API
  }, [currentPage, pageSize]);

  const handleNewProductSave = () => {
    // Lưu sản phẩm mới logic
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <Button variant="primary" onClick={() => setShowModal(true)}>
        Add New Product
      </Button>

      {/* Modal New Product */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Form nhập sản phẩm */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={handleNewProductSave}
            disabled={isSaveDisabled}
          >
            Save
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Product Table */}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>
                {/* Product Edit Logic */}
              </td>
              <td>
                {/* Category Edit Logic */}
              </td>
              <td>
                {/* Description Edit Logic */}
              </td>
              <td>
                {/* Price Edit Logic */}
              </td>
              <td>
                {/* Image Edit Logic */}
              </td>
              <td>
                {/* Actions Logic */}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Pagination */}
      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
