import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form } from 'react-bootstrap';

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]); // Danh sách categories
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null); // Lưu hình ảnh đã chọn

  // Hàm gọi API để lấy danh sách categories
  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:5203/api/Category');
      setCategories(response.data); // Cập nhật danh sách categories
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  // Hàm gọi API để lấy danh sách sản phẩm
  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axios.get('http://localhost:5203/api/Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items); // Lưu sản phẩm vào state
        setTotalPages(response.data.totalPages); // Lưu tổng số trang
      } else {
        console.error('Dữ liệu không hợp lệ', response.data);
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories(); // Tải danh sách categories khi component mount
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleEditClick = (productId) => {
    setIsEditing(productId);
  };

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    const updatedProducts = products.map((product) =>
      product.productId === productId ? { ...product, [name]: value } : product
    );
    setProducts(updatedProducts);
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    const updatedProducts = products.map((product) =>
      product.productId === productId ? { ...product, category: { categoryId: value } } : product
    );
    setProducts(updatedProducts);
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      // Set hình ảnh đã chọn vào state để hiển thị
      setSelectedImage(URL.createObjectURL(file));

      // Upload hình ảnh lên server
      const formData = new FormData();
      formData.append("image", file);

      axios.post("http://localhost:5203/api/Product/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      }).then(response => {
        const updatedProducts = products.map(product =>
          product.productId === productId ? { ...product, productImageUrl: response.data.imageUrl } : product
        );
        setProducts(updatedProducts);
      }).catch(error => {
        console.error("Có lỗi khi upload hình ảnh!", error);
      });
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find(p => p.productId === productId);

    // Kiểm tra xem category đã được chọn đúng chưa, nếu không thì giữ giá trị cũ
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId // Nếu chưa chọn category mới, giữ categoryId cũ
    };

    try {
      // Gọi API để lưu sản phẩm
      await axios.put(`http://localhost:5203/api/Product/${productId}`, updatedProduct);

      // Cập nhật lại sản phẩm sau khi lưu thành công
      fetchProducts(currentPage, pageSize); // Load lại danh sách sản phẩm từ server
      setIsEditing(null); // Dừng chỉnh sửa
      setSelectedImage(null); // Reset hình ảnh đã chọn
    } catch (error) {
      console.error("Có lỗi khi cập nhật sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null); // Reset hình ảnh đã chọn khi cancel
  };

  return (
    <div>
      <h2>Manage Products</h2>
      <Button variant="primary" className="mb-3" onClick={() => alert('Create new product')}>Create Product</Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products && products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ''} // Đảm bảo categoryId được lưu
                      onChange={(e) => handleCategoryChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category ? product.category.categoryName : 'N/A'
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productPrice
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <div>
                      {/* Hidden input file */}
                      <input
                        type="file"
                        id={`fileInput_${product.productId}`}
                        style={{ display: 'none' }} // Ẩn input file
                        onChange={(e) => handleImageChange(e, product.productId)}
                      />
                      <Button 
                        variant="light" 
                        size="sm"
                        onClick={() => document.getElementById(`fileInput_${product.productId}`).click()}
                        style={{
                          backgroundColor: '#d3d3d3',  // Màu xám nhạt khi không hover
                          fontSize: '0.75rem',
                          border: 'none', // Xóa border mặc định
                          transition: 'background-color 0.3s ease', // Hiệu ứng chuyển màu mượt mà khi hover
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = '#b0b0b0'} // Màu xám đậm hơn khi hover
                        onMouseOut={(e) => e.target.style.backgroundColor = '#d3d3d3'} // Quay lại màu xám nhạt khi bỏ chuột ra
                      >
                        Browse
                      </Button>
                      {selectedImage && (
                        <Image src={selectedImage} alt="Selected" thumbnail width={100} />
                      )}
                    </div>
                  ) : (
                    product.productImageUrl && (
                      <Image
                        src={`http://localhost:5072${product.productImageUrl}`}
                        alt={product.productName}
                        fluid
                        width={50}
                      />
                    )
                  )}
                </td>
                <td>{product.createdDate ? new Date(product.createdDate).toLocaleDateString() : 'N/A'}</td>
                <td>{product.updatedDate ? new Date(product.updatedDate).toLocaleDateString() : 'N/A'}</td>
                <td>
                  {isEditing === product.productId ? (
                    <div className="d-flex gap-2">
                      <Button variant="success" size="sm" onClick={() => handleSaveClick(product.productId)}>Save</Button>
                      <Button variant="secondary" size="sm" onClick={handleCancelClick}>Cancel</Button>
                    </div>
                  ) : (
                    <Button variant="warning" size="sm" onClick={() => handleEditClick(product.productId)}>Edit</Button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="9">No products found</td>
            </tr>
          )}
        </tbody>
      </Table>

      <Pagination>
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index + 1}
            active={index + 1 === currentPage}
            onClick={() => handlePageChange(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
