import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false); // Modal for creating product
  const [newProduct, setNewProduct] = useState({
    productName: '',
    categoryId: '',
    productDescription: '',
    productPrice: '',
    productImageUrl: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login');
    }
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, category: { categoryId: value } } : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find((p) => p.productId === productId);
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId,
    };

    try {
      await axiosInstance.put(`Product/${productId}`, updatedProduct);
      fetchProducts(currentPage, pageSize);
      setIsEditing(null);
      setSelectedImage(null);
    } catch (error) {
      console.error("Có lỗi khi cập nhật sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null);
  };

  // Handle creating new product
  const handleCreateProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleCreateProductSave = async () => {
    if (!newProduct.productName || !newProduct.categoryId) {
      alert('Product Name and Category are mandatory!');
      return;
    }

    try {
      await axiosInstance.post('Product', newProduct);
      fetchProducts(currentPage, pageSize);
      setShowCreateModal(false);
      setNewProduct({
        productName: '',
        categoryId: '',
        productDescription: '',
        productPrice: '',
        productImageUrl: ''
      });
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm mới!", error);
    }
  };

  const handleCreateProductClose = () => {
    setShowCreateModal(false);
    setNewProduct({
      productName: '',
      categoryId: '',
      productDescription: '',
      productPrice: '',
      productImageUrl: ''
    });
  };

  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Create Product
        </Button>
      )}
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>CreatedDate</th>
            <th>UpdatedDate</th>
            {isLoggedIn && isAdmin && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productName"
                      value={product.productName}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      as="select"
                      name="category"
                      value={product.category?.categoryId || ''}
                      onChange={(e) => handleCategoryChange(e, product.productId)}
                    >
                      {categories.map((category) => (
                        <option key={category.categoryId} value={category.categoryId}>
                          {category.categoryName}
                        </option>
                      ))}
                    </Form.Control>
                  ) : (
                    product.category?.categoryName || 'N/A'
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="text"
                      name="productDescription"
                      value={product.productDescription}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    product.productDescription
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <Form.Control
                      type="number"
                      name="productPrice"
                      value={product.productPrice}
                      onChange={(e) => handleInputChange(e, product.productId)}
                    />
                  ) : (
                    `$${product.productPrice}`
                  )}
                </td>
                <td>
                  {isEditing === product.productId ? (
                    <>
                      <input
                        type="file"
                        onChange={(e) => handleImageChange(e, product.productId)}
                      />
                      {selectedImage && <Image src={selectedImage} alt="Product" width={100} />}
                    </>
                  ) : (
                    product.productImageUrl && (
                      <Image
                        src={product.productImageUrl}
                        alt={product.productName}
                        width={100}
                      />
                    )
                  )}
                </td>
                <td>{product.createdDate}</td>
                <td>{product.updatedDate}</td>
                {isLoggedIn && isAdmin && (
                  <td>
                    {isEditing === product.productId ? (
                      <>
                        <Button
                          variant="success"
                          onClick={() => handleSaveClick(product.productId)}
                        >
                          Save
                        </Button>
                        <Button
                          variant="danger"
                          onClick={handleCancelClick}
                          className="ms-2"
                        >
                          Cancel
                        </Button>
                      </>
                    ) : (
                      <Button
                        variant="warning"
                        onClick={() => handleEditClick(product.productId)}
                      >
                        Edit
                      </Button>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="8">No products found</td>
            </tr>
          )}
        </tbody>
      </Table>

      {/* Pagination */}
      <Pagination>
        <Pagination.Prev
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
        />
        {[...Array(totalPages)].map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
        <Pagination.Next
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
        />
      </Pagination>

      {/* Create Product Modal */}
      <Modal show={showCreateModal} onHide={handleCreateProductClose}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                name="productName"
                value={newProduct.productName}
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group controlId="formCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                name="categoryId"
                value={newProduct.categoryId}
                onChange={handleCreateProductChange}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="formProductDescription">
              <Form.Label>Product Description</Form.Label>
              <Form.Control
                type="text"
                name="productDescription"
                value={newProduct.productDescription}
                onChange={handleCreateProductChange}
              />
            </Form.Group>
            <Form.Group controlId="formProductPrice">
              <Form.Label>Product Price</Form.Label>
              <Form.Control
                type="number"
                name="productPrice"
                value={newProduct.productPrice}
                onChange={handleCreateProductChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCreateProductClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCreateProductSave}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductPage;
