import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Table, Pagination, Image, Form, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

// Setup Axios Instance
const token = localStorage.getItem('token');
const roles = JSON.parse(localStorage.getItem('roles'));

const axiosInstance = axios.create({
  baseURL: 'http://localhost:5203/api/',
});

// Custom function to add token for POST/PUT requests
axiosInstance.interceptors.request.use(
  (config) => {
    if (config.method === 'post' || config.method === 'put') {
      if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(10);
  const [isEditing, setIsEditing] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // State to track if user is an admin
  const [showCreateModal, setShowCreateModal] = useState(false); // State to show/hide create product modal
  const [newProduct, setNewProduct] = useState({ productName: '', categoryId: '', productDescription: '', productPrice: '', productImageUrl: '' });
  const navigate = useNavigate(); // useNavigate hook for redirecting

  // Check if the user is logged in and if the user is an admin
  useEffect(() => {
    if (token && roles && roles.includes('Admin User')) {
      setIsLoggedIn(true);
      setIsAdmin(true);
    } else {
      setIsLoggedIn(false);
      setIsAdmin(false);
      navigate('/login'); // Redirect to login page if not logged in or not admin
    }
  }, [navigate]);

  // Fetch Data
  const fetchCategories = async () => {
    try {
      const response = await axiosInstance.get('Category');
      setCategories(response.data);
    } catch (error) {
      console.error("Có lỗi khi fetch categories!", error);
    }
  };

  const fetchProducts = async (pageNumber = 1, pageSize = 10) => {
    try {
      const response = await axiosInstance.get('Product/pagination', {
        params: { pageNumber, pageSize },
      });

      if (response.data && response.data.items && Array.isArray(response.data.items)) {
        setProducts(response.data.items);
        setTotalPages(response.data.totalPages);
      } else {
        setProducts([]);
      }
    } catch (error) {
      console.error("Có lỗi khi fetch sản phẩm!", error);
      setProducts([]);
    }
  };

  useEffect(() => {
    fetchCategories();
    fetchProducts(currentPage, pageSize);
  }, [currentPage, pageSize]);

  // Handlers
  const handleEditClick = (productId) => setIsEditing(productId);

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, [name]: value } : product
      )
    );
  };

  const handleCategoryChange = (e, productId) => {
    const { value } = e.target;
    setProducts((prev) =>
      prev.map((product) =>
        product.productId === productId ? { ...product, category: { categoryId: value } } : product
      )
    );
  };

  const handleImageChange = (e, productId) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
      const formData = new FormData();
      formData.append("image", file);

      axiosInstance
        .post("Product/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => {
          setProducts((prev) =>
            prev.map((product) =>
              product.productId === productId
                ? { ...product, productImageUrl: response.data.imageUrl }
                : product
            )
          );
        })
        .catch((error) => {
          console.error("Có lỗi khi upload hình ảnh!", error);
        });
    }
  };

  const handleSaveClick = async (productId) => {
    const product = products.find((p) => p.productId === productId);
    const updatedProduct = {
      ...product,
      categoryId: product.category?.categoryId || product.categoryId,
    };

    try {
      await axiosInstance.put(`Product/${productId}`, updatedProduct);
      fetchProducts(currentPage, pageSize);
      setIsEditing(null);
      setSelectedImage(null);
    } catch (error) {
      console.error("Có lỗi khi cập nhật sản phẩm!", error);
    }
  };

  const handleCancelClick = () => {
    setIsEditing(null);
    setSelectedImage(null);
  };

  const handleCreateClick = () => setShowCreateModal(true); // Show modal for creating product
  const handleCloseModal = () => setShowCreateModal(false); // Close modal
  const handleCreateProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('productName', newProduct.productName);
      formData.append('categoryId', newProduct.categoryId);
      formData.append('productDescription', newProduct.productDescription);
      formData.append('productPrice', newProduct.productPrice);
      formData.append('productImageUrl', newProduct.productImageUrl);

      await axiosInstance.post('Product', formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      fetchProducts(currentPage, pageSize);
      setShowCreateModal(false); // Close modal after creating product
    } catch (error) {
      console.error("Có lỗi khi tạo sản phẩm mới!", error);
    }
  };

  // Render UI
  return (
    <div>
      <h2>Manage Products</h2>
      {isLoggedIn && isAdmin && ( // Only show Create Product button if logged in and is admin
        <Button variant="primary" className="mb-3" onClick={handleCreateClick}>
          Create Product
        </Button>
      )}
      
      {/* Modal for Create Product */}
      <Modal show={showCreateModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Create New Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product name"
                value={newProduct.productName}
                onChange={(e) => setNewProduct({ ...newProduct, productName: e.target.value })}
                required
              />
            </Form.Group>

            <Form.Group controlId="category">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                value={newProduct.categoryId}
                onChange={(e) => setNewProduct({ ...newProduct, categoryId: e.target.value })}
                required
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.categoryId} value={category.categoryId}>
                    {category.categoryName}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="productDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter product description"
                value={newProduct.productDescription}
                onChange={(e) => setNewProduct({ ...newProduct, productDescription: e.target.value })}
              />
            </Form.Group>

            <Form.Group controlId="productPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter product price"
                value={newProduct.productPrice}
                onChange={(e) => setNewProduct({ ...newProduct, productPrice: e.target.value })}
                required
              />
            </Form.Group>

            <Form.Group controlId="productImage">
              <Form.Label>Product Image</Form.Label>
              <Form.Control
                type="file"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    setNewProduct({ ...newProduct, productImageUrl: URL.createObjectURL(file) });
                  }
                }}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCreateProduct}>
            Create Product
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Table to display products */}
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Category</th>
            <th>Description</th>
            <th>Price</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>
                {isEditing === product.productId ? (
                  <input
                    type="text"
                    name="productName"
                    value={product.productName}
                    onChange={(e) => handleInputChange(e, product.productId)}
                  />
                ) : (
                  product.productName
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <select
                    name="categoryId"
                    value={product.category.categoryId}
                    onChange={(e) => handleCategoryChange(e, product.productId)}
                  >
                    {categories.map((category) => (
                      <option key={category.categoryId} value={category.categoryId}>
                        {category.categoryName}
                      </option>
                    ))}
                  </select>
                ) : (
                  product.category.categoryName
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <input
                    type="text"
                    name="productDescription"
                    value={product.productDescription}
                    onChange={(e) => handleInputChange(e, product.productId)}
                  />
                ) : (
                  product.productDescription
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <input
                    type="number"
                    name="productPrice"
                    value={product.productPrice}
                    onChange={(e) => handleInputChange(e, product.productId)}
                  />
                ) : (
                  product.productPrice
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <input
                    type="file"
                    onChange={(e) => handleImageChange(e, product.productId)}
                  />
                ) : (
                  <Image
                    src={product.productImageUrl}
                    alt="Product"
                    width={50}
                    height={50}
                  />
                )}
              </td>
              <td>
                {isEditing === product.productId ? (
                  <>
                    <Button variant="success" onClick={() => handleSaveClick(product.productId)}>
                      Save
                    </Button>
                    <Button variant="secondary" onClick={handleCancelClick}>
                      Cancel
                    </Button>
                  </>
                ) : (
                  <Button variant="primary" onClick={() => handleEditClick(product.productId)}>
                    Edit
                  </Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Pagination */}
      <Pagination>
        {Array.from({ length: totalPages }).map((_, index) => (
          <Pagination.Item
            key={index}
            active={index + 1 === currentPage}
            onClick={() => setCurrentPage(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default ProductPage;
